INSERT [dbo].[ProductReviews] ([ProductId], [Channel], [Comment], [CreatedTime]) VALUES (7, N'渠道1', N'价格：双十一前买的，号称什么全程保价，价格保住了，赠品花样百出的还是亏了200左右。配置：m3乞丐版对我来说足矣，十多年来买电脑我从来不看中cpu，128g内置+64tf拓展。编码制图等重活都留给**** pro了。问题：莫名其妙的卡死及死机好像不是什么新鲜事，期待win10更新了。续航：3小时多吧，毕竟你不可能只开word打打字，总要听听音乐。BTW：买了一堆配件，触控笔、****本家蓝牙鼠，HDMI转接线，基本齐活了。', CAST(N'2016-12-03 20:48:31.000' AS DateTime))
