INSERT [dbo].[ProductReviews] ([ProductId], [Channel], [Comment], [CreatedTime]) VALUES (7, N'渠道2', N'移动办公很方便，之前用的是pro3 双十一的时候m3特价买的，虽然是m3的处理器不过用起来很顺畅，比pro3 i3要快，用鲁大师测试看到硬盘超越99%的电脑应该是pcie的ssd的功劳，同时无风扇的设计使用起来也很安静，日常办公，写写代码，完全没有问题，如果是渲染视频等工作的话就有点跟不上了，作为出门机配合onedrive同步到主力机使用起来很方便', CAST(N'2016-11-29 18:45:10.000' AS DateTime))
