INSERT [dbo].[ProductReviews] ([ProductId], [Channel], [Comment], [CreatedTime]) VALUES (1, N'渠道1', N'东西已收到！还没有打开测试，但先评论一下收货的感受！收到的物流包装很令人担心！首先装 平板电脑 pro的纸箱不符合物品的尺寸，导致纸箱边缘被压裂。让人很担心途中是否对物品造成压力损伤！其二键盘是没有额外物流盒包装的，只是和赠品装在一个物流袋里。可是键盘的自带包装盒正面是塑料片，让人担心键盘在运送过程中如果有收到撞击，是否会造成产品质量问题！希望可以给官网和****建议：既然是自营店，为什么不统一包装在一个合适的尺寸物流箱中再出仓库？我们公司的仓库针对客户的产品从来没有这样的问题！希望改进，尤其在e-commerce逐渐成为主流的情况下，希望重视各环节！！！', CAST(N'2016-12-15 11:47:38.000' AS DateTime))
