INSERT [dbo].[ProductReviews] ([ProductId], [Channel], [Comment], [CreatedTime]) VALUES (7, N'渠道2', N'双11下的单，天天盼，时时盼，终于在约定时间拿到手，包装很不错，一看就是正品。回家就激活了，装了大型软件，跑得不错，小巧便携实用，这几天一直在用，台式机、本本都被冷落了。但发现，电池待机时间不像简介里说的可以有9小时呀，我几本每天都在充电，看电影四个小时左右，今天充满录像，录了2小时，只剩10%左右的电了。。这点有点伤心。还有就是，电影看久一点，就有噪音，还挺大的。总的来说，电池和散热好像不是很好哦。', CAST(N'2016-11-24 18:22:18.000' AS DateTime))
